<script>
var xhr;
function load_data(){
	var jsonRow="return_preferences_json.php?action=wishlist";
	if(xhr) xhr.abort();
	xhr=$.getJSON(jsonRow,function(result){

		if(result.error){
			$('#products_section').html('<div class="alert alert-danger" role="alert">'+result.error+'</div>');
			$('#img_loading_div').hide();
		}else{
			var htmlStr="";		
			$.each(result.aaData, function(i,item){
				htmlStr+='<div CLASS="row wishlist-row" >';
				var linkStr='gb_fn_linkCacheHandlerJS(\'product.php?uuid='+item.id+'\',\'product.php?uuid='+item.id+'\')';
          		var buyCodeStr=item.id, buyStr="uuid";
          		if(item.code){
          			linkStr='gb_fn_linkCacheHandlerJS(\''+item.code+'.html\',\'product.php?code='+item.code+'\')';
          			buyCodeStr=item.code;
          			buyStr="code";
          		}
            	htmlStr+='<div class="col-sm-3 text-center"><a onClick="'+linkStr+'" href="javascript:void(0)"><img src="'+item.image+'" class="img-responsive" alt="'+item.name+'" STYLE="height:170px;" onerror="this.src=\'images/default-product-small.png\'"></a></div>';
              	htmlStr+='<div class="col-sm-6"><a onClick="'+linkStr+'" href="javascript:void(0)" class="prdt-name">'+item.name+'</a><br><strong>Product SKU</strong>: '+item.sku+'<br>';
              	htmlStr+='<span class="price">'+item.currency+item.price+'</span></div>';
              	htmlStr+='<div class="col-sm-3 text-center"><a onclick="enquire_about(\''+buyStr+'\', \''+buyCodeStr+'\')" href="javascript:void(0)" class="buy-now-btn" title="Enquire Now"> Enquire Now</a><br/><a HREF="javascript:void(0)" onClick="remove_wishlist(\''+item.id+'\')" title="Remove from wishlist" class="rem-frm-list">Remove from wishlist</a></div>';
            	htmlStr+='</div>';
            }); 
			
			$('#products_section').append(htmlStr);
			$('#img_loading_div').hide();
		}
	});
}

function remove_wishlist(id){
	$(".alert").remove();
	$.ajax({
		url: 'removeUserPreferences.php',
		type: 'POST',
		data: {"uuid" : id, "action" : "wishlist"},
		dataType: 'json',
		cache: false,
		success: function(response){
			if(response.success){
				$('#products_section').html("");
				$('#img_loading_div').show();
				load_data();
				fetchUserPreferences("wishlist");
			}else if(response.error){
				$("#products_section").before('<div class="alert alert-danger" role="alert">'+response.error+'</div>');
			}				
		}
	});
}
$(function () {
	load_data();	
});
</script>
